package cl.rodrigo_javier_garrido_dagle.mobiledevtest.ui.navigationimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport cl.rodrigo_javier_garrido_dagle.mobiledevtest.MainViewModelimport cl.rodrigo_javier_garrido_dagle.mobiledevtest.domain.model.Hitimport cl.rodrigo_javier_garrido_dagle.mobiledevtest.ui.screens.DetailScreenimport cl.rodrigo_javier_garrido_dagle.mobiledevtest.ui.screens.MainListScreen@Composablefun AppNavigation(    viewModel: MainViewModel,    hits: List<Hit>) {    val navController = rememberNavController()    NavHost(        navController = navController,        startDestination = AppScreens.MainListScreen.route    ) {        composable(AppScreens.MainListScreen.route) {            MainListScreen(                navController, viewModel,                Modifier,                hits            )        }        composable(AppScreens.DetailScreen.route) {            DetailScreen(navController, viewModel)        }    }}