package cl.rodrigo_javier_garrido_dagle.mobiledevtest.ui.navigationimport androidx.compose.runtime.Composableimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport cl.rodrigo_javier_garrido_dagle.mobiledevtest.MainViewModelimport cl.rodrigo_javier_garrido_dagle.mobiledevtest.ui.screens.DetailScreenimport cl.rodrigo_javier_garrido_dagle.mobiledevtest.ui.screens.MainListScreen@Composablefun AppNavigation(   viewModel: MainViewModel,) {   val navController = rememberNavController()   NavHost(      navController = navController,      startDestination = AppScreens.MainListScreen.route   ) {      composable(AppScreens.DetailScreen.route) {         MainListScreen(            navController,            viewModel,         )      }      composable(AppScreens.MainListScreen.route) {         DetailScreen(            navController,            viewModel,         )      }   }}